function popupImage(){const a=document.getElementById('js-popup');if(!a)return;const c=document.getElementById('js-black-bg'),d=document.getElementById('js-close-btn'),e=document.getElementsByClassName('js-show-popup');b(c),b(d);for(const a of e)f(a);function b(b){if(!b)return;b.addEventListener('click',()=>{a.classList.toggle('is-show'),slideshow.resume()})}function f(b){if(!b)return;const c=b.getElementsByTagName("img");for(const b of c)b.addEventListener('click',()=>{const c=b.src;if(!c)return;popedImg.src=c,a.classList.toggle('is-show'),slideshow.pause()})}}const popedImg=document.getElementById('js-popup-img');popupImage()